/**
 * @title \u5927\u6570\u636E
 */
export default () => {
  const [data] = React.useState(() => {
    // \u6A21\u62DF 10^4 \u4E2A\u6570\u636E\u91CF
    function dig(path = "0", level) {
      const list: any = []
      for (let i = 0; i < 5; i += 1) {
        const id = \`\${path}-\${i}\`
        const treeNode = {
          title: id,
          id,
          children: [] as any[],
        }

        if (level > 0) {
          treeNode.children = dig(id, level - 1)
        }

        list.push(treeNode)
      }
      return list
    }

    const treeData = dig("0", 4)
    return treeData
  })

  return (
    <>
      <h1>virtualList</h1>
      <div className="check-tree-select-virtual-list__wrap">
        <CheckTreeSelect
          style={{ width: 240 }}
          data={data}
          checkedMode="PARENT"
          onChange={console.log}
          virtual
          height={260}
        />
      </div>
    </>
  )
}`,title:"\u5927\u6570\u636E",description:"null",mdxType:"CodeBlock"}),mdx("h2",t({},{id:"props"}),"Props"),mdx("h3",t({},{id:"checktreeselect-props"}),"CheckTreeSelect Props"),mdx("table",null,mdx("thead",{parentName:"table"},mdx("tr",{parentName:"thead"},mdx("th",t({parentName:"tr"},{align:null}),"\u53C2\u6570"),mdx("th",t({parentName:"tr"},{align:null}),"\u8BF4\u660E"),mdx("th",t({parentName:"tr"},{align:null}),"\u7C7B\u578B"),mdx("th",t({parentName:"tr"},{align:null}),"\u53EF\u9009\u503C"),mdx("th",t({parentName:"tr"},{align:null}),"\u9ED8\u8BA4\u503C"))),mdx("tbody",{parentName:"table"},mdx("tr",{parentName:"tbody"},mdx("td",t({parentName:"tr"},{align:null}),"data"),mdx("td",t({parentName:"tr"},{align:null}),"\u5C55\u793A\u6570\u636E"),mdx("td",t({parentName:"tr"},{align:null}),"CheckTreeSelectDataItem","[","]"),mdx("td",t({parentName:"tr"},{align:null}),"-"),mdx("td",t({parentName:"tr"},{align:null}),"[","]")),mdx("tr",{parentName:"tbody"},mdx("td",t({parentName:"tr"},{align:null}),"fieldNames"),mdx("td",t({parentName:"tr"},{align:null}),"\u8BBE\u7F6E data \u4E2D id, title, disabled, children \u5BF9\u5E94\u7684 key (3.0 \u65B0\u589E)	object	-	{ title: 'title', id: 'id',disabled:'disabled', children: 'children'}"),mdx("td",t({parentName:"tr"},{align:null}),"Record<string, string",">"),mdx("td",t({parentName:"tr"},{align:null}),"-"),mdx("td",t({parentName:"tr"},{align:null}),'"{} as any"')),mdx("tr",{parentName:"tbody"},mdx("td",t({parentName:"tr"},{align:null}),"checkedMode"),mdx("td",t({parentName:"tr"},{align:null}),"\u591A\u9009\u6570\u636E\u4EA4\u4E92\u65F6\u56DE\u586B\u3001\u56DE\u663E\u6A21\u5F0F","PARENT: \u5F53\u6240\u6709\u5B50\u8282\u70B9\u88AB\u9009\u4E2D\u65F6\u5C06\u53EA\u4FDD\u7559\u7236\u8282\u70B9","ALL: \u6240\u6709\u88AB\u9009\u4E2D\u8282\u70B9\uFF0C\u4E0D\u533A\u5206\u7236\u5B50\u8282\u70B9\uFF08\u4E0D\u652F\u6301\u5F02\u6B65\u6570\u636E\u52A0\u8F7D\u52FE\u9009checkbox\uFF09","CHILD: \u4EC5\u663E\u793A\u5B50\u8282\u70B9\uFF08\u4E0D\u652F\u630