/**
 * @title \u5927\u6570\u636E
 */
export default () => {
  const [data] = React.useState(() => {
    // \u6A21\u62DF 10^4 \u4E2A\u6570\u636E\u91CF
    function dig(path = "0", level) {
      const list: any = []
      for (let i = 0; i < 5; i += 1) {
        const id = \`\${path}-\${i}\`
        const treeNode = {
          title: id,
          id,
          children: [] as any[],
        }

        if (level > 0) {
          treeNode.children = dig(id, level - 1)
        }

        list.push(treeNode)
      }
      return list
    }

    const treeData = dig("0", 4)
    return treeData
  })

  return (
    <>
      <h1>virtualList</h1>
      <div className="check-tree-select-virtual-list__wrap">
        <CheckTreeSelect
          style={{ width: 240 }}
          data={data}
          checkedMode="PARENT"
          onChange={console.log}
          virtual
          height={260}
        />
      </div>
    </>
  )
}`,title:"\u5927\u6570\u636E",description:"null",mdxType:"CodeBlock"}))}MDXContent.isMDXComponent=!0;
