import Tree, { TreeHelper } from "@hi-ui/tree"
import Button from "@hi-ui/button"

/**
 * @title \u8BBE\u7F6E\u6EDA\u52A8\u4F4D\u7F6E
 * @desc \u4EC5\u5728\u8BBE\u7F6E height \u5C5E\u6027\u5373\u865A\u62DF\u5217\u8868\u4E0B\u6709\u6548
 */
export const ScrollTo = () => {
  const treeRef = React.useRef<TreeHelper>(null)

  const [treeData] = React.useState(() => {
    function dig(path = "0", level) {
      const list: { title: string; id: string; children: any[] }[] = []

      for (let i = 0; i < 5; i += 1) {
        const id = \`\${path}-\${i}\`
        const treeNode = {
          title: id,
          id,
          children: [] as any[],
        }

        if (level > 0) {
          treeNode.children = dig(id, level - 1)
        }

        list.push(treeNode)
      }
      return list
    }

    const treeData = dig("0", 4)
    return treeData
  })

  return (
    <>
      <h1>ScrollTo for Tree</h1>
      <div className="tree-scroll-to__wrap">
        <Button
          onClick={() => {
            // key \u4E3A\u8282\u70B9 id
            treeRef.current?.scrollTo?.({ key: "0-2-2-0-0", align: "top" })
          }}
        >
          scroll to key: 0-2-2-0-0
        </Button>
        <Tree
          innerRef={treeRef}
          height={300}
          defaultExpandAll
          data={treeData}
          expandOnSelect
        ></Tree>
      </div>
    </>
  )
}`,title:"\u8BBE\u7F6E\u6EDA\u52A8\u4F4D\u7F6E",description:"\u4EC5\u5728\u8BBE\u7F6E height \u5C5E\u6027\u5373\u865A\u62DF\u5217\u8868\u4E0B\u6709\u6548",mdxType:"CodeBlock"}),mdx("h3",t({},{id:"\u81EA\u5B9A\u4E49-icon-\u6E32\u67D3\u51FD\u6570"}),"\u81EA\u5B9A\u4E49 icon \u6E32\u67D3\u51FD\u6570"),mdx(CodeBlock,{code:`import React from "react"
