  const [treeData] = React.useState(() => {
    function dig(path = "0", level) {
      const list = []
      for (let i = 0; i < 10; i += 1) {
        const id = \`\${path}-\${i}\`
        const treeNode = {
          title: id,
          id,
          children: [] as any[],
        }

        if (level > 0) {
          treeNode.children = dig(id, level - 1)
        }

        list.push(treeNode)
      }
      return list
    }

