                      if (!value) {
                        callback("\u8BF7\u8F93\u5165\u624B\u673A\u53F7")
                      } else if (!telReg.test(value)) {
                        callback("\u8BF7\u8F93\u5165\u6B63\u786E\u7684\u9A8C\u8BC1\u7801")
                      } else {
                        callback()
                      }
                    },
                  },
