              Drawer
            </Button>
            <Button type="secondary" onClick={() => setModalVisible(!modalVisible)}>
              Modal
            </Button>
            <Button
              type="secondary"
              onClick={() => {
                message.open({
                  title: "\u6B22\u8FCE\u4F7F\u7528 HiUI \u7EC4\u4EF6\u5E93",
                  type: "success",
                })
              }}
            >
              Message
            </Button>
            <Button
              type="secondary"
              onClick={() => {
                notification.open({
                  size: "sm",
                  title: "\u6570\u636E\u5907\u4EFD\u901A\u77E5",
                  content:
                    "\u5404\u4F4D\u540C\u5B66\u8BF7\u6CE8\u610F\uFF0C\u5C06\u4E8E2019.08.10 00:00:00 -08:00\uFF1A00 \u671F\u95F4\u8FDB\u884C\u7CFB\u7EDF\u670D\u52A1\u5668\u5347\u7EA7\u7EF4\u62A4\uFF01",
                })
